const Products = () => {
    const [activeFilter, setActiveFilter] = useState('all');
  const { addToCart } = useCart();
  const [addedItems, setAddedItems] = useState({});
  const categories = [
    'all',
    'bread',
    'pastries',
    'cookies',
    'muffins',
    'cupcakes',
    'scones',
    'sweet-bread'
  ];
  const filteredProducts = activeFilter === 'all' 
    ? products 
    : products.filter(product => product.category === activeFilter);
  const handleAddToCart = (product) => {
    addToCart({ ...product, quantity: 1 });
    setAddedItems(prev => ({ ...prev, [product.id]: true }));
    setTimeout(() => setAddedItems(prev => ({ ...prev, [product.id]: false })), 2000);
  };
  return (
    <section className="products-section" id="menu">
      <div className="container">
        <div className="section-header">
          <h2>Our Fresh Baked Goods</h2>
          <p>Made fresh daily with the finest ingredients</p>
          <a href="#products" className="view-menu-btn">View Full Menu </a>
        </div>
        
        <div className="product-filters">
          {categories.map(category => (
            <button
              key={category}
              className={`filter-btn ${activeFilter === category ? 'active' : ''}`}
              onClick={() => setActiveFilter(category)}
            >
              {category.charAt(0).toUpperCase() + category.slice(1).replace('-', ' ')}
            </button>
          ))}
        </div>
        <div className="products-grid" id="products">
          {filteredProducts.map((product) => (
            <div className="product-card" key={product.id}>
              <div className="product-image">
                <img src={product.image} alt={product.name} />
                <button 
                  className={`add-to-cart-btn ${addedItems[product.id] ? 'added' : ''}`}
                  onClick={() => handleAddToCart(product)}
                >
                  {addedItems[product.id] ? 'Added!' : 'Add to Cart'}
                </button>
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <p className="product-description">{product.description}</p>
                <div className="product-footer">
                  <span className="price">{Math.round(product.price)}</span>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>
    </section>
  );
};

export default Products;